import { useState, useEffect } from "react";
import API from "../../api";

export default function AddParticipantForm() {
  const [form, setForm] = useState({
    name: "",
    college: "",
    education: "",
    social_link: "",
    accommodation_status: "pending",
    travel_status: "pending",
    registration_id: "",
  });

  const [participants, setParticipants] = useState([]);
  const [editingId, setEditingId] = useState(null);

  useEffect(() => {
    fetchParticipants();
  }, []);

  const fetchParticipants = async () => {
    const res = await API.get("/participants");
    setParticipants(res.data);
  };

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (editingId) {
      await API.put(`/participants/${editingId}`, form);
      alert("✅ Participant updated successfully!");
    } else {
      await API.post("/participants", form);
      alert("✅ Participant added successfully!");
    }
    setForm({
      name: "",
      college: "",
      education: "",
      social_link: "",
      accommodation_status: "pending",
      travel_status: "pending",
      registration_id: "",
    });
    setEditingId(null);
    fetchParticipants();
  };

  const handleDelete = async (id) => {
    if (window.confirm("Delete this participant?")) {
      await API.delete(`/participants/${id}`);
      alert("🗑️ Deleted successfully!");
      fetchParticipants();
    }
  };

  const handleEdit = (p) => {
    setForm(p);
    setEditingId(p._id);
  };

  return (
    <div className="space-y-6">
      <h2 className="text-2xl font-bold">Manage Participants</h2>

      {/* ✏️ Form */}
      <form onSubmit={handleSubmit} className="space-y-3">
        <input
          name="name"
          value={form.name}
          onChange={handleChange}
          placeholder="Full Name"
          className="w-full p-2 border rounded"
        />
        <input
          name="college"
          value={form.college}
          onChange={handleChange}
          placeholder="College"
          className="w-full p-2 border rounded"
        />
        <input
          name="education"
          value={form.education}
          onChange={handleChange}
          placeholder="Education"
          className="w-full p-2 border rounded"
        />
        <input
          name="social_link"
          value={form.social_link}
          onChange={handleChange}
          placeholder="Social Link"
          className="w-full p-2 border rounded"
        />
        <input
          name="registration_id"
          value={form.registration_id}
          onChange={handleChange}
          placeholder="Registration ID"
          className="w-full p-2 border rounded"
        />
        <div className="flex gap-4">
          <select
            name="accommodation_status"
            value={form.accommodation_status}
            onChange={handleChange}
            className="p-2 border rounded"
          >
            <option value="pending">Accommodation Pending</option>
            <option value="confirmed">Accommodation Confirmed</option>
            <option value="rejected">Accommodation Rejected</option>
          </select>
          <select
            name="travel_status"
            value={form.travel_status}
            onChange={handleChange}
            className="p-2 border rounded"
          >
            <option value="pending">Travel Pending</option>
            <option value="confirmed">Travel Confirmed</option>
            <option value="rejected">Travel Rejected</option>
          </select>
        </div>
        <button
          type="submit"
          className={`px-4 py-2 rounded text-white ${
            editingId
              ? "bg-yellow-500 hover:bg-yellow-600"
              : "bg-blue-600 hover:bg-blue-700"
          }`}
        >
          {editingId ? "Update Participant" : "Add Participant"}
        </button>
      </form>

      {/* 📋 Participant list */}
      <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-4 mt-5">
        {participants.map((p) => (
          <div
            key={p._id}
            className="bg-white shadow p-4 rounded border border-gray-200"
          >
            <h4 className="text-lg font-semibold">{p.name}</h4>
            <p className="text-gray-600 text-sm">
              {p.college} — {p.education}
            </p>
            <p className="text-sm text-gray-500">
              Travel: {p.travel_status}, Accommodation:{" "}
              {p.accommodation_status}
            </p>
            <div className="flex gap-2 mt-2">
              <button
                onClick={() => handleEdit(p)}
                className="bg-yellow-400 hover:bg-yellow-500 px-3 py-1 rounded"
              >
                ✏️ Edit
              </button>
              <button
                onClick={() => handleDelete(p._id)}
                className="bg-red-600 hover:bg-red-700 text-white px-3 py-1 rounded"
              >
                🗑️ Delete
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
