import { useEffect, useState, useRef } from "react";
import API from "../api";

export default function AboutPage() {
  const [about, setAbout] = useState(null);
  const galleryRef = useRef(null);
  const autoSlideRef = useRef();

  useEffect(() => {
    const fetchAbout = async () => {
      try {
        const res = await API.get("/about");
        setAbout(res.data);
      } catch (err) {
        console.error(err);
      }
    };
    fetchAbout();
  }, []);

  if (!about) return <p>Loading...</p>;

  const baseURL = "http://localhost:5000";

  const LazyImage = ({ src, alt, style }) => {
    const [loaded, setLoaded] = useState(false);
    const finalSrc = src.startsWith("http") ? src : baseURL + src;

    return (
      <div
        style={{
          backgroundColor: "#e0e0e0",
          borderRadius: "8px",
          minWidth: "250px",
          height: "180px",
          flex: "0 0 auto",
          overflow: "hidden",
          ...style,
        }}
      >
        <img
          src={finalSrc}
          alt={alt}
          loading="lazy"
          onLoad={() => setLoaded(true)}
          style={{
            width: "100%",
            height: "100%",
            objectFit: "cover",
            opacity: loaded ? 1 : 0,
            transition: "opacity 0.5s ease-in-out",
            display: "block",
          }}
        />
      </div>
    );
  };

  // Auto-slide
  useEffect(() => {
    if (!about?.gallery?.length) return;

    const slide = () => {
      if (galleryRef.current) {
        const scrollWidth = galleryRef.current.scrollWidth;
        const clientWidth = galleryRef.current.clientWidth;
        if (galleryRef.current.scrollLeft + clientWidth >= scrollWidth) {
          galleryRef.current.scrollTo({ left: 0, behavior: "smooth" });
        } else {
          galleryRef.current.scrollBy({ left: 260, behavior: "smooth" });
        }
      }
    };

    autoSlideRef.current = setInterval(slide, 3000);

    return () => clearInterval(autoSlideRef.current);
  }, [about]);

  // Pause on hover
  const handleMouseEnter = () => clearInterval(autoSlideRef.current);
  const handleMouseLeave = () => {
    autoSlideRef.current = setInterval(() => {
      if (galleryRef.current) {
        const scrollWidth = galleryRef.current.scrollWidth;
        const clientWidth = galleryRef.current.clientWidth;
        if (galleryRef.current.scrollLeft + clientWidth >= scrollWidth) {
          galleryRef.current.scrollTo({ left: 0, behavior: "smooth" });
        } else {
          galleryRef.current.scrollBy({ left: 260, behavior: "smooth" });
        }
      }
    }, 3000);
  };

  const scrollGallery = (direction) => {
    if (galleryRef.current) {
      galleryRef.current.scrollBy({
        left: direction === "left" ? -260 : 260,
        behavior: "smooth",
      });
    }
  };

  return (
    <div className="container my-5">
      {/* Poster */}
      {about.poster && (
        <LazyImage
          src={about.poster}
          alt="Poster"
          style={{ width: "100%", height: "400px", marginBottom: "20px" }}
        />
      )}

      {/* Description */}
      <p className="mb-4">{about.description}</p>

      {/* Gallery Carousel */}
      {about.gallery.length > 0 && (
        <div
          style={{ position: "relative", marginBottom: "20px" }}
          onMouseEnter={handleMouseEnter}
          onMouseLeave={handleMouseLeave}
        >
          {/* Left Arrow */}
          <button
            onClick={() => scrollGallery("left")}
            style={{
              position: "absolute",
              left: 0,
              top: "50%",
              transform: "translateY(-50%)",
              zIndex: 10,
              background: "#0008",
              color: "#fff",
              border: "none",
              borderRadius: "50%",
              width: "36px",
              height: "36px",
              cursor: "pointer",
            }}
          >
            ◀
          </button>

          {/* Right Arrow */}
          <button
            onClick={() => scrollGallery("right")}
            style={{
              position: "absolute",
              right: 0,
              top: "50%",
              transform: "translateY(-50%)",
              zIndex: 10,
              background: "#0008",
              color: "#fff",
              border: "none",
              borderRadius: "50%",
              width: "36px",
              height: "36px",
              cursor: "pointer",
            }}
          >
            ▶
          </button>

          <div
            ref={galleryRef}
            style={{
              display: "flex",
              overflowX: "auto",
              gap: "16px",
              padding: "10px 40px",
              scrollSnapType: "x mandatory",
              scrollBehavior: "smooth",
            }}
          >
            {about.gallery.map((img, idx) => (
              <div key={idx} style={{ scrollSnapAlign: "start" }}>
                <LazyImage src={img} alt={`Gallery ${idx}`} />
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Schedule */}
      <h3 className="mt-4">Schedule</h3>
      <ul className="list-group mb-4">
        {about.schedule.map((s, idx) => (
          <li key={idx} className="list-group-item">
            {s.date} - {s.event} ({s.time})
          </li>
        ))}
      </ul>

      {/* Sponsors */}
      <h3>Sponsors</h3>
      <div
        style={{
          display: "flex",
          gap: "16px",
          overflowX: "auto",
          paddingBottom: "10px",
        }}
      >
        {about.sponsors.map((s, idx) => (
          <div key={idx} style={{ flex: "0 0 auto", textAlign: "center" }}>
            <LazyImage src={s.logo} alt={s.name} style={{ width: "120px", height: "80px", borderRadius: "8px" }} />
            <p className="small mt-1">{s.name}</p>
          </div>
        ))}
      </div>
    </div>
  );
}
