import { useState, useEffect } from "react";
import API from "../../api";

export default function AddCoordinatorForm() {
  const [form, setForm] = useState({ name: "", designation: "", contact: "" });
  const [imageFile, setImageFile] = useState(null);
  const [coordinators, setCoordinators] = useState([]);
  const [editingId, setEditingId] = useState(null);

  useEffect(() => {
    fetchCoordinators();
  }, []);

  const fetchCoordinators = async () => {
    try {
      const res = await API.get("/coordinators");
      setCoordinators(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const formData = new FormData();
      formData.append("name", form.name);
      formData.append("designation", form.designation);
      formData.append("contact", form.contact);
      if (imageFile) formData.append("image", imageFile);

      if (editingId) {
        await API.put(`/coordinators/${editingId}`, formData, {
          headers: { "Content-Type": "multipart/form-data" },
        });
        alert("‚úÖ Coordinator updated successfully!");
      } else {
        await API.post("/coordinators", formData, {
          headers: { "Content-Type": "multipart/form-data" },
        });
        alert("‚úÖ Coordinator added successfully!");
      }

      setForm({ name: "", designation: "", contact: "" });
      setImageFile(null);
      setEditingId(null);
      fetchCoordinators();
    } catch (err) {
      console.error("Error submitting form:", err);
      alert("‚ùå Error adding/updating coordinator");
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm("Delete this coordinator?")) {
      await API.delete(`/coordinators/${id}`);
      alert("üóëÔ∏è Coordinator deleted!");
      fetchCoordinators();
    }
  };

  const handleEdit = (c) => {
    setForm({ name: c.name, designation: c.designation, contact: c.contact });
    setEditingId(c._id);
    setImageFile(null);
  };

  return (
    <div className="space-y-6 px-4 md:px-8">
      <h2 className="text-2xl font-bold text-center md:text-left">Manage Coordinators</h2>

      {/* ‚úèÔ∏è Add/Edit Form */}
      <form onSubmit={handleSubmit} className="flex flex-col md:flex-row md:items-center md:gap-4 gap-3">
        <input
          name="name"
          value={form.name}
          onChange={handleChange}
          placeholder="Coordinator Name"
          className="p-2 border rounded flex-1"
        />
        <input
          name="designation"
          value={form.designation}
          onChange={handleChange}
          placeholder="Designation"
          className="p-2 border rounded flex-1"
        />
        <input
          name="contact"
          value={form.contact}
          onChange={handleChange}
          placeholder="Contact"
          className="p-2 border rounded flex-1"
        />
        <input type="file" onChange={(e) => setImageFile(e.target.files[0])} className="border rounded p-2" />
        {imageFile && (
          <img
            src={URL.createObjectURL(imageFile)}
            alt="Preview"
            className="w-24 h-24 object-cover rounded mt-2 md:mt-0"
          />
        )}
        <button
          type="submit"
          className={`px-4 py-2 rounded text-white ${
            editingId ? "bg-yellow-500 hover:bg-yellow-600" : "bg-blue-600 hover:bg-blue-700"
          }`}
        >
          {editingId ? "Update" : "Add"}
        </button>
      </form>
      
      {/* üìã Coordinator List */}
<div className="mt-8 grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-5 gap-6 place-items-center">
  {coordinators.map((c) => (
    <div
      key={c._id}
      className="bg-white shadow-lg hover:shadow-xl transition-all duration-300 border border-gray-200  p-4 flex flex-col items-center text-center w-20 max-w-[180px]"
    >
      {/* üñºÔ∏è Image */}
      {c.image && (
        <div className="relative w-full aspect-[3/4] overflow-hidden rounded-xl mb-3">
          <img
            src={`http://localhost:5000${c.image}`}
            alt={c.name}
            className="w-full h-full object-cover object-top"
          />
        </div>
      )}

      {/* üß† Details */}
      <h4 className="text-sm sm:text-base font-semibold text-gray-800">{c.name}</h4>
      <p className="text-xs text-gray-600">{c.designation}</p>
      <p className="text-[11px] text-gray-500 mt-1">{c.contact}</p>

      {/* üß© Buttons */}
      <div className="flex gap-2 mt-3">
        <button
          onClick={() => handleEdit(c)}
          className="bg-yellow-400 hover:bg-yellow-500 text-[11px] px-2 py-[2px] rounded-md font-medium transition"
        >
          ‚úèÔ∏è
        </button>
        <button
          onClick={() => handleDelete(c._id)}
          className="bg-red-600 hover:bg-red-700 text-white text-[11px] px-2 py-[2px] rounded-md font-medium transition"
        >
          üóëÔ∏è
        </button>
      </div>
    </div>
  ))}
</div>

    </div>
  );
}
