import { useState, useEffect } from "react";
import { CircularProgressbar, buildStyles } from "react-circular-progressbar";
import "react-circular-progressbar/dist/styles.css";
import "./Countdown.css"; // extra styling

export default function AdvancedCountdown({ targetDate }) {
  const [timeLeft, setTimeLeft] = useState({});

  useEffect(() => {
    const interval = setInterval(() => {
      const now = new Date().getTime();
      const distance = new Date(targetDate).getTime() - now;

      if (distance < 0) {
        clearInterval(interval);
        setTimeLeft({ expired: true });
      } else {
        setTimeLeft({
          days: Math.floor(distance / (1000 * 60 * 60 * 24)),
          hours: Math.floor((distance / (1000 * 60 * 60)) % 24),
          minutes: Math.floor((distance / (1000 * 60)) % 60),
          seconds: Math.floor((distance / 1000) % 60),
        });
      }
    }, 1000);

    return () => clearInterval(interval);
  }, [targetDate]);

  if (timeLeft.expired) {
    return <div className="text-danger text-center display-5 mt-4">Event Started!</div>;
  }

  const timeBlocks = [
    { label: "Days", value: timeLeft.days, max: 365 },
    { label: "Hours", value: timeLeft.hours, max: 24 },
    { label: "Minutes", value: timeLeft.minutes, max: 60 },
    { label: "Seconds", value: timeLeft.seconds, max: 60 },
  ];

  return (
    <div className="d-flex flex-wrap justify-content-center gap-4 mt-4">
      {timeBlocks.map((block, idx) => (
        <div key={idx} className="circular-card text-center p-2">
          <CircularProgressbar
            value={block.value}
            maxValue={block.max}
            text={`${block.value}`}
            styles={buildStyles({
              pathColor: `#6a11cb`,
              textColor: "#fff",
              trailColor: "#2575fc",
              textSize: "24px",
              pathTransitionDuration: 0.5,
            })}
          />
          <div className="mt-2 text-white fw-bold">{block.label}</div>
        </div>
      ))}
    </div>
  );
}
