import { useState, useEffect } from "react";
import API from "../../api";

export default function AddCoordinatorForm() {
  const [form, setForm] = useState({
    name: "",
    designation: "",
    contact: "",
  });
  const [imageFile, setImageFile] = useState(null);
  const [coordinators, setCoordinators] = useState([]);
  const [editingId, setEditingId] = useState(null);

  useEffect(() => {
    fetchCoordinators();
  }, []);

  const fetchCoordinators = async () => {
    try {
      const res = await API.get("/coordinators");
      setCoordinators(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const formData = new FormData();
      formData.append("name", form.name);
      formData.append("designation", form.designation);
      formData.append("contact", form.contact);
      if (imageFile) formData.append("image", imageFile);

      if (editingId) {
        await API.put(`/coordinators/${editingId}`, formData, {
          headers: { "Content-Type": "multipart/form-data" },
        });
        alert("✅ Coordinator updated successfully!");
      } else {
        await API.post("/coordinators", formData, {
          headers: { "Content-Type": "multipart/form-data" },
        });
        alert("✅ Coordinator added successfully!");
      }

      setForm({ name: "", designation: "", contact: "" });
      setImageFile(null);
      setEditingId(null);
      fetchCoordinators();
    } catch (err) {
      console.error("Error submitting form:", err);
      alert("❌ Error adding/updating coordinator");
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm("Delete this coordinator?")) {
      await API.delete(`/coordinators/${id}`);
      alert("🗑️ Coordinator deleted!");
      fetchCoordinators();
    }
  };

  const handleEdit = (c) => {
    setForm({
      name: c.name,
      designation: c.designation,
      contact: c.contact,
    });
    setEditingId(c._id);
    // Optionally show current image as preview
    setImageFile(null);
  };

  return (
    <div className="space-y-6">
      <h2 className="text-2xl font-bold">Manage Coordinators</h2>

      {/* ✏️ Add/Edit Form */}
      <form onSubmit={handleSubmit} className="d-flex flex-column gap-3">
        <input
          name="name"
          value={form.name}
          onChange={handleChange}
          placeholder="Coordinator Name"
          className="p-2 border rounded"
        />
        <input
          name="designation"
          value={form.designation}
          onChange={handleChange}
          placeholder="Designation"
          className="p-2 border rounded"
        />
        <input
          name="contact"
          value={form.contact}
          onChange={handleChange}
          placeholder="Contact"
          className="p-2 border rounded"
        />
        <input
          type="file"
          onChange={(e) => setImageFile(e.target.files[0])}
        />
        {imageFile && (
          <img
            src={URL.createObjectURL(imageFile)}
            alt="Preview"
            className="w-32 h-32 object-cover mt-2"
          />
        )}
        <button
          type="submit"
          className={`px-4 py-2 rounded text-white ${
            editingId ? "bg-yellow-500" : "bg-blue-600"
          }`}
        >
          {editingId ? "Update Coordinator" : "Add Coordinator"}
        </button>
      </form>

      {/* 📋 Coordinator list */}
      <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-4 mt-5">
        {coordinators.map((c) => (
          <div
            key={c._id}
            className="bg-white shadow p-4 rounded border border-gray-200"
          >
            {c.image && (
              <img
                src={`http://localhost:5000${c.image}`}
                alt={c.name}
                className="w-full h-auto object-cover rounded mb-2"
              />
            )}
            <h4 className="text-lg font-semibold">{c.name}</h4>
            <p className="text-gray-600">{c.designation}</p>
            <p className="text-gray-500 text-sm mt-1">{c.contact}</p>
            <div className="flex gap-2 mt-2">
              <button
                onClick={() => handleEdit(c)}
                className="bg-yellow-400 px-3 py-1 rounded"
              >
                ✏️ Edit
              </button>
              <button
                onClick={() => handleDelete(c._id)}
                className="bg-red-600 text-white px-3 py-1 rounded"
              >
                🗑️ Delete
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
