import React, { useEffect, useState } from "react";
import API from "../api"; // axios instance

export default function Participants() {
  const [participants, setParticipants] = useState([]);
  const [newPart, setNewPart] = useState({
    name: "",
    email: "",
    phone: "",
    college: "",
    department: "",
    year: "",
    social_link: "",
    events: [],
  });

  useEffect(() => {
    fetchParticipants();
  }, []);

  const fetchParticipants = async () => {
    const res = await API.get("/participants");
    setParticipants(res.data);
  };

  const handleChange = (e) => {
    setNewPart({ ...newPart, [e.target.name]: e.target.value });
  };

  const handleAdd = async (e) => {
    e.preventDefault();
    try {
      await API.post("/participants", newPart);
      fetchParticipants();
      setNewPart({
        name: "",
        email: "",
        phone: "",
        college: "",
        department: "",
        year: "",
        social_link: "",
        events: [],
      });
    } catch (err) {
      alert("Error adding participant");
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm("Delete this participant?")) return;
    await API.delete(`/participants/${id}`);
    fetchParticipants();
  };

  return (
    <div className="container my-5">
      <h2 className="text-center text-primary fw-bold mb-4">
        Participants List 👩‍🔬👨‍🔬
      </h2>

      {/* List */}
      <div className="table-responsive">
        <table className="table table-striped table-hover shadow-sm">
          <thead className="table-dark">
            <tr>
              <th>Name</th>
              <th>Email</th>
              <th>College</th>
              <th>Event(s)</th>
              <th>Accommodation</th>
              <th>Travel</th>
              <th>Reg ID</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {participants.map((p) => (
              <tr key={p._id}>
                <td>{p.name}</td>
                <td>{p.email}</td>
                <td>{p.college}</td>
                <td>{p.events.map((ev) => ev.name).join(", ")}</td>
                <td>{p.accommodation_status}</td>
                <td>{p.travel_status}</td>
                <td>{p.registration_id}</td>
                <td>
                  <button
                    onClick={() => handleDelete(p._id)}
                    className="btn btn-sm btn-outline-danger"
                  >
                    🗑️ Delete
                  </button>
                </td>             
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Add New */}
      <div className="card mt-5 shadow-sm">
        <div className="card-body">
          <h4 className="text-center mb-3">Add New Participant</h4>
          <form
            onSubmit={handleAdd}
            className="d-flex flex-column align-items-center gap-3"
          >
            <input
              type="text"
              className="form-control w-75"
              placeholder="Full Name"
              name="name"
              value={newPart.name}
              onChange={handleChange}
            />
            <input
              type="email"
              className="form-control w-75"
              placeholder="Email"
              name="email"
              value={newPart.email}
              onChange={handleChange}
            />
            <input
              type="text"
              className="form-control w-75"
              placeholder="College"
              name="college"
              value={newPart.college}
              onChange={handleChange}
            />
            <input
              type="text"
              className="form-control w-75"
              placeholder="Department"
              name="department"
              value={newPart.department}
              onChange={handleChange}
            />
            <input
              type="text"
              className="form-control w-75"
              placeholder="Year"
              name="year"
              value={newPart.year}
              onChange={handleChange}
            />
            <input
              type="url"
              className="form-control w-75"
              placeholder="LinkedIn / Social Link"
              name="social_link"
              value={newPart.social_link}
              onChange={handleChange}
            />
            <button type="submit" className="btn btn-success px-5">
              Add Participant
            </button>
          </form>
        </div>
      </div>
    </div>
  );
}
