import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";

export default function Coordinators() {
  const [coordinators, setCoordinators] = useState([
    { id: 1, name: "Aarav Patil", role: "Event Lead", email: "aarav@iitb.ac.in" },
    { id: 2, name: "Nisha Kulkarni", role: "Design Head", email: "nisha@iitb.ac.in" },
    { id: 3, name: "Rohan Joshi", role: "Publicity Manager", email: "rohan@iitb.ac.in" },
  ]);

  const [newCoord, setNewCoord] = useState({
    name: "",
    role: "",
    email: "",
  });

  const handleChange = (e) => {
    setNewCoord({ ...newCoord, [e.target.name]: e.target.value });
  };

  const handleAdd = (e) => {
    e.preventDefault();
    if (!newCoord.name || !newCoord.role || !newCoord.email) {
      alert("Please fill all fields");
      return;
    }
    setCoordinators([
      ...coordinators,
      { id: coordinators.length + 1, ...newCoord },
    ]);
    setNewCoord({ name: "", role: "", email: "" });
  };

  const handleDelete = (id) => {
    setCoordinators(coordinators.filter((coord) => coord.id !== id));
  };

  return (
    <div className="container my-5">
      <h2 className="text-center mb-4 fw-bold text-primary">
        Meet Our Coordinators ðŸ‘¥
      </h2>

      {/* Coordinator Cards */}
      <div className="row">
        {coordinators.map((coord) => (
          <div className="col-md-4 mb-4" key={coord.id}>
            <div className="card shadow-sm h-100 border-0">
              <div className="card-body text-center">
                <h5 className="card-title fw-bold">{coord.name}</h5>
                <p className="card-text text-secondary mb-1">{coord.role}</p>
                <p className="text-muted small">{coord.email}</p>
                <button
                  className="btn btn-outline-danger btn-sm"
                  onClick={() => handleDelete(coord.id)}
                >
                  Remove
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* Add Coordinator Form */}
      <div className="card mt-5 shadow-sm border-0">
        <div className="card-body">
          <h4 className="mb-3 text-center">Add New Coordinator</h4>
          <form
            onSubmit={handleAdd}
            className="d-flex flex-column align-items-center gap-3"
          >
            <input
              type="text"
              className="form-control w-75"
              placeholder="Full Name"
              name="name"
              value={newCoord.name}
              onChange={handleChange}
            />
            <input
              type="text"
              className="form-control w-75"
              placeholder="Role (e.g., Event Lead)"
              name="role"
              value={newCoord.role}
              onChange={handleChange}
            />
            <input
              type="email"
              className="form-control w-75"
              placeholder="Email"
              name="email"
              value={newCoord.email}
              onChange={handleChange}
            />
            <button type="submit" className="btn btn-success px-4">
              Add Coordinator
            </button>
          </form>
        </div>
      </div>
    </div>
  );
}
