import { useState, useEffect } from "react";
import API from "../../api";

export default function AddCoordinatorForm() {
  const [form, setForm] = useState({
    name: "",
    designation: "",
    contact: "",
    image: "",
  });
  const [coordinators, setCoordinators] = useState([]);
  const [editingId, setEditingId] = useState(null);

  useEffect(() => {
    fetchCoordinators();
  }, []);

  const fetchCoordinators = async () => {
    const res = await API.get("/coordinators");
    setCoordinators(res.data);
  };

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (editingId) {
      await API.put(`/coordinators/${editingId}`, form);
      alert("✅ Coordinator updated successfully!");
    } else {
      await API.post("/coordinators", form);
      alert("✅ Coordinator added successfully!");
    }
    setForm({
      name: "",
      designation: "",
      contact: "",
      image: "",
    });
    setEditingId(null);
    fetchCoordinators();
  };

  const handleDelete = async (id) => {
    if (window.confirm("Delete this coordinator?")) {
      await API.delete(`/coordinators/${id}`);
      alert("🗑️ Coordinator deleted!");
      fetchCoordinators();
    }
  };

  const handleEdit = (c) => {
    setForm(c);
    setEditingId(c._id);
  };

  return (
    <div className="space-y-6">
      <h2 className="text-2xl font-bold">Manage Coordinators</h2>

      {/* ✏️ Add/Edit Form */}
      <form onSubmit={handleSubmit} className="d-flex flex-column align-items-center gap-3">
        <input
          name="name"
          value={form.name}
          onChange={handleChange}
          placeholder="Coordinator Name"
          className="col-md-6 p-4 border rounded"
        />
        <input
          name="designation"
          value={form.designation}
          onChange={handleChange}
          placeholder="Designation"
          className="col-md-6 p-4 border rounded"
        />
        <input
          name="contact"
          value={form.contact}
          onChange={handleChange}
          placeholder="Contact"
          className="col-md-6 p-4 border rounded"
        />
        <input
          name="image"
          value={form.image}
          onChange={handleChange}
          placeholder="Image URL"
          className="col-md-6 p-4 border rounded"
        />
        <button
          type="submit"
          className={`px-4 py-2 rounded text-white ${
            editingId
              ? "bg-yellow-500 hover:bg-yellow-600"
              : "bg-blue-600 hover:bg-blue-700"
          }`}
        >
          {editingId ? "Update Coordinator" : "Add Coordinator"}
        </button>
      </form>

      {/* 📋 Coordinator list */}
      <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-4 mt-5">
        {coordinators.map((c) => (
          <div
            key={c._id}
            className="bg-white shadow p-4 rounded border border-gray-200"
          >
            {c.image && (
              <img
                src={c.image}
                alt={c.name}
                className="w-full h-40 object-cover rounded mb-2"
              />
            )}
            <h4 className="text-lg font-semibold">{c.name}</h4>
            <p className="text-gray-600">{c.designation}</p>
            <p className="text-gray-500 text-sm mt-1">{c.contact}</p>
            <div className="flex gap-2 mt-2">
              <button
                onClick={() => handleEdit(c)}
                className="bg-yellow-400 hover:bg-yellow-500 px-3 py-1 rounded"
              >
                ✏️ Edit
              </button>
              <button
                onClick={() => handleDelete(c._id)}
                className="bg-red-600 hover:bg-red-700 text-white px-3 py-1 rounded"
              >
                🗑️ Delete
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
