import { useEffect, useState } from "react";
import API from "../../api"; // ✅ correct path to your axios instance

export default function EditAbout() {
  const [form, setForm] = useState({
    poster: "",
    description: "",
    gallery: [],
    schedule: [{ date: "", event: "", time: "" }],
    sponsors: [{ name: "", logo: "" }],
  });

  const [posterFile, setPosterFile] = useState(null);
  const [galleryFiles, setGalleryFiles] = useState([]);

  // 🧠 Fetch About data
  useEffect(() => {
    const fetchAbout = async () => {
      try {
        const res = await API.get("/about");
        setForm(res.data);
      } catch (err) {
        console.error("Error fetching about:", err);
      }
    };
    fetchAbout();
  }, []);

  // 🖊️ Handle text changes
  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  // 🗓️ Schedule Handlers
  const handleScheduleChange = (index, field, value) => {
    const newSchedule = [...form.schedule];
    newSchedule[index][field] = value;
    setForm({ ...form, schedule: newSchedule });
  };

  const addSchedule = () =>
    setForm({
      ...form,
      schedule: [...form.schedule, { date: "", event: "", time: "" }],
    });

  const removeSchedule = (index) => {
    const newSchedule = form.schedule.filter((_, i) => i !== index);
    setForm({ ...form, schedule: newSchedule });
  };

  // 💰 Sponsor Handlers
  const handleSponsorChange = (index, field, value) => {
    const newSponsors = [...form.sponsors];
    newSponsors[index][field] = value;
    setForm({ ...form, sponsors: newSponsors });
  };

  const addSponsor = () =>
    setForm({
      ...form,
      sponsors: [...form.sponsors, { name: "", logo: "" }],
    });

  const removeSponsor = (index) => {
    const newSponsors = form.sponsors.filter((_, i) => i !== index);
    setForm({ ...form, sponsors: newSponsors });
  };

  // 💾 Submit Handler
  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const formData = new FormData();
      if (posterFile) formData.append("poster", posterFile);
      for (let i = 0; i < galleryFiles.length; i++) {
        formData.append("gallery", galleryFiles[i]);
      }
      formData.append("description", form.description);
      formData.append("schedule", JSON.stringify(form.schedule));
      for (let i = 0; i < form.sponsors.length; i++) {
  if (form.sponsors[i].file) {
    formData.append("sponsors", form.sponsors[i].file);
  }
}

      await API.put("/about", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      alert("✅ About page updated successfully!");
    } catch (err) {
      console.error("Error updating about:", err);
      alert("❌ Error updating About Page");
    }
  };

  return (
    <div className="container my-5">
      <h3 className="mb-4 fw-bold text-primary">🪩 Edit About Page</h3>

      <form onSubmit={handleSubmit}>
        {/* 🖼️ Poster */}
        <div className="mb-3">
          <label className="fw-semibold">Poster</label>
          <input
            type="file"
            onChange={(e) => setPosterFile(e.target.files[0])}
            className="form-control"
          />
          {form.poster && (
            <img src={`http://localhost:5000${form.poster}`} alt="Poster" />
          )}
        </div>

        {/* 📝 Description */}
        <div className="mb-4">
          <label className="fw-semibold">Description</label>
          <textarea
            name="description"
            value={form.description}
            onChange={handleChange}
            className="form-control"
            rows="4"
          />
        </div>

        {/* 🖼️ Gallery */}
        <h5 className="fw-bold">Gallery</h5>
        <input
          type="file"
          multiple
          onChange={(e) => setGalleryFiles(e.target.files)}
          className="form-control mb-3"
        />

        <div className="d-flex flex-wrap gap-2 mb-4">
  {form.gallery?.map((img, idx) => (
    <img
      key={idx}
      src={`http://localhost:5000${img}`}  // ✅ add this prefix
      alt={`Gallery ${idx}`}
      className="rounded shadow-sm"
      style={{ width: "150px", height: "100px", objectFit: "cover" }}
    />
  ))}
</div>

        {/* 📅 Schedule */}
        <h5 className="fw-bold">Schedule</h5>
        {form.schedule.map((s, idx) => (
          <div key={idx} className="d-flex gap-2 mb-2">
            <input
              type="date"
              value={s.date}
              onChange={(e) =>
                handleScheduleChange(idx, "date", e.target.value)
              }
              className="form-control"
            />
            <input
              type="text"
              placeholder="Event Name"
              value={s.event}
              onChange={(e) =>
                handleScheduleChange(idx, "event", e.target.value)
              }
              className="form-control"
            />
            <input
              type="time"
              value={s.time}
              onChange={(e) =>
                handleScheduleChange(idx, "time", e.target.value)
              }
              className="form-control"
            />
            <button
              type="button"
              onClick={() => removeSchedule(idx)}
              className="btn btn-danger"
            >
              ❌
            </button>
          </div>
        ))}
        <button
          type="button"
          onClick={addSchedule}
          className="btn btn-outline-primary mb-4"
        >
          + Add Schedule
        </button>

        {/* 💼 Sponsors */}
        <h5 className="fw-bold">Sponsors</h5>
{form.sponsors.map((s, idx) => (
  <div key={idx} className="mb-3">
    <input
      type="text"
      placeholder="Sponsor Name"
      value={s.name}
      onChange={(e) =>
        handleSponsorChange(idx, "name", e.target.value)
      }
      className="form-control mb-2"
    />
    <input
      type="file"
      onChange={(e) => {
        const newSponsors = [...form.sponsors];
        newSponsors[idx].file = e.target.files[0];
        setForm({ ...form, sponsors: newSponsors });
      }}
      className="form-control mb-2"
    />
    {s.logo && (
      <img
        src={`http://localhost:5000${s.logo}`}
        alt={s.name}
        className="rounded"
        style={{ width: "120px", height: "80px", objectFit: "contain" }}
      />
    )}
    <button
      type="button"
      onClick={() => removeSponsor(idx)}
      className="btn btn-danger btn-sm mt-1"
    >
      ❌ Remove
    </button>
  </div>
))}

<button
  type="button"
  onClick={addSponsor}
  className="btn btn-outline-primary mb-4"
>
  + Add Sponsor
</button>

        <div className="text-center">
          <button type="submit" className="btn btn-success px-5 fw-semibold">
            💾 Save Changes
          </button>
        </div>
      </form>
    </div>
  );
}
