import "bootstrap/dist/css/bootstrap.min.css";
import { useState, useEffect } from "react";
import API from "../api"; // axios instance
import "../css/coordinators.css"; // ðŸ”¥ Add CSS here for flip effect

export default function Coordinators() {
  const [coordinators, setCoordinators] = useState([]);
  const [flipped, setFlipped] = useState({}); // store flip state for each card

  useEffect(() => {
    const fetchCoordinators = async () => {
      try {
        const res = await API.get("/coordinators");
        setCoordinators(res.data);
      } catch (err) {
        console.error("Error fetching coordinators:", err);
      }
    };
    fetchCoordinators();
  }, []);

  const handleFlip = (id) => {
    setFlipped((prev) => ({ ...prev, [id]: !prev[id] }));
  };

  return (
    <div className="container my-5">
      <h2 className="text-center mb-4 fw-bold text-primary">
        Meet Our Coordinators ðŸ‘¥
      </h2>

      <div className="row g-4">
        {coordinators.map((c) => (
          <div key={c._id} className="col-md-6 col-lg-3">
            <div
              className={`flip-card ${flipped[c._id] ? "flipped" : ""}`}
              onClick={() => handleFlip(c._id)}
            >
              <div className="flip-card-inner shadow rounded">
                {/* FRONT */}
                <div className="flip-card-front bg-white border border-gray-200 rounded">
                  {c.image && (
                    <img
                      src={`http://localhost:5000${c.image}`}
                      alt={c.name}
                      className="card-img-top img-fluid rounded"
                      style={{ height: "300px", objectFit: "cover" }}
                    />
                  )}
                </div>

                {/* BACK */}
                <div className="flip-card-back d-flex flex-column justify-content-center align-items-center text-center bg-primary text-white rounded">
                  <h4 className="fw-semibold">{c.name}</h4>
                  <p className="mb-1">{c.designation}</p>
                  <p className="small">{c.contact}</p>
                  <p className="mt-2 text-light small">(Click to flip back)</p>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
