import { useState } from "react";
import API from "../../api";

export default function AddEventForm() {
  const [form, setForm] = useState({
    name: "",
    date: "",
    venue: "",
  });
  const [imageFile, setImageFile] = useState(null);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("name", form.name);
    formData.append("date", form.date);
    formData.append("venue", form.venue);
    if (imageFile) formData.append("image", imageFile);

    console.log("üì§ Uploading event:", formData.get("name"));
    if (imageFile) console.log("üì∏ Image appended:", imageFile.name);

    try {
      const res = await API.post("/events", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      console.log("‚úÖ Event created:", res.data);
      alert("Event added successfully!");
    } catch (err) {
      console.error("‚ùå Error uploading event:", err);
      alert("Failed to upload event!");
    }
  };

  return (
    <form
      onSubmit={handleSubmit}
      className="max-w-md mx-auto mt-10 p-6 bg-white rounded-2xl shadow-lg space-y-5"
    >
      <h2 className="text-2xl font-bold text-center text-gray-800">
        Add New Event
      </h2>

      <div>
        <label className="block text-gray-700 font-medium mb-1">Event Name</label>
        <input
          type="text"
          name="name"
          value={form.name}
          onChange={handleChange}
          className="w-full border border-gray-300 rounded-lg px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
          placeholder="Enter event name"
        />
      </div>

      <div>
        <label className="block text-gray-700 font-medium mb-1">Date</label>
        <input
          type="date"
          name="date"
          value={form.date}
          onChange={handleChange}
          className="w-full border border-gray-300 rounded-lg px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
        />
      </div>

      <div>
        <label className="block text-gray-700 font-medium mb-1">Venue</label>
        <input
          type="text"
          name="venue"
          value={form.venue}
          onChange={handleChange}
          className="w-full border border-gray-300 rounded-lg px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
          placeholder="Enter venue"
        />
      </div>

      <div>
        <label className="block text-gray-700 font-medium mb-1">Event Image</label>
        <input
          type="file"
          onChange={(e) => setImageFile(e.target.files[0])}
          className="w-full border border-gray-300 rounded-lg px-3 py-2 file:mr-3 file:py-2 file:px-4 file:rounded-md file:border-0 file:text-sm file:bg-blue-50 file:text-blue-700 hover:file:bg-blue-100"
        />
        {imageFile && (
          <img
            src={URL.createObjectURL(imageFile)}
            alt="Preview"
            className="mt-3 rounded-xl shadow-md w-48 h-60 object-cover"
          />
        )}
      </div>

      <button
        type="submit"
        className="w-full bg-blue-600 text-white font-semibold py-2 rounded-lg hover:bg-blue-700 transition"
      >
        Submit
      </button>
    </form>
  );
}
