import { useEffect, useState } from "react";
import API from "../api";

export default function AboutPage() {
  const [about, setAbout] = useState(null);

  useEffect(() => {
    const fetchAbout = async () => {
      try {
        const res = await API.get("/about");
        setAbout(res.data);
      } catch (err) {
        console.error(err);
      }
    };
    fetchAbout();
  }, []);

  if (!about) return <p>Loading...</p>;

  return (
    <div className="container my-5">
      {/* Poster */}
      {about.poster && (
        <img src={about.poster} alt="Poster" className="w-100 mb-4" />
      )}

      {/* Description */}
      <p className="mb-4">{about.description}</p>

      {/* Gallery */}
      <div className="row g-3 mb-4">
        {about.gallery.map((img, idx) => (
          <div key={idx} className="col-md-4">
            <img src={img} alt={`Gallery ${idx}`} className="w-100 rounded" />
          </div>
        ))}
      </div>

      {/* Schedule */}
      <h3>Schedule</h3>
      <ul className="list-group mb-4">
        {about.schedule.map((s, idx) => (
          <li key={idx} className="list-group-item">
            {s.date} - {s.event} ({s.time})
          </li>
        ))}
      </ul>

      {/* Sponsors */}
      <h3>Sponsors</h3>
      <div className="d-flex flex-wrap gap-3">
        {about.sponsors.map((s, idx) => (
          <div key={idx} className="text-center">
            <img
              src={s.logo}
              alt={s.name}
              className="rounded mb-1"
              style={{ width: "120px", height: "auto" }}
            />
            <p className="small">{s.name}</p>
          </div>
        ))}
      </div>
    </div>
  );
}
