import { Link } from "react-router-dom";
import { useEffect, useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "../css/navbar.css";

export default function Navbar() {
  const [scrollY, setScrollY] = useState(0);

  useEffect(() => {
    const handleScroll = () => setScrollY(window.scrollY);
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  // Scroll प्रमाणे transparency कमी-जास्त करायची
  const opacity = Math.min(scrollY / 200, 0.85); // जास्तीत जास्त 0.85
  const navbarStyle = {
    backgroundColor: `rgba(0, 0, 0, ${opacity})`,
    backdropFilter: opacity > 0.1 ? "blur(10px)" : "none",
    transition: "background-color 0.3s ease, backdrop-filter 0.3s ease",
  };

  return (
    <>
      {/* Fullscreen Hero Section */}
      <section className="hero-section">
        <div className="overlay">
          <h1 className="hero-text">Welcome to GenVision 2025</h1>
        </div>
      </section>

      {/* Navbar */}
      <nav className="navbar navbar-expand-lg navbar-dark fixed-top" style={navbarStyle}>
        <div className="container">
          <h1 className="navbar-brand fw-bold">GenVision 2025</h1>
          <button
            className="navbar-toggler"
            type="button"
            data-bs-toggle="collapse"
            data-bs-target="#navbarNav"
            aria-controls="navbarNav"
            aria-expanded="false"
            aria-label="Toggle navigation"
          >
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse justify-content-end" id="navbarNav">
            <ul className="navbar-nav">
              <li className="nav-item"><Link to="/" className="nav-link">About</Link></li>
              <li className="nav-item"><Link to="/Events" className="nav-link">Events</Link></li>
              <li className="nav-item"><Link to="/Participants" className="nav-link">Participate</Link></li>
              <li className="nav-item"><Link to="/Coordinators" className="nav-link">Coordinators</Link></li>
              <li className="nav-item"><Link to="/Guests" className="nav-link">Guests</Link></li>
            </ul>
          </div>
        </div>
      </nav>

      {/* Content Section */}
      <section className="content-section">
        <div className="container py-5 text-white">
          <h2>About GenVision</h2>
          <p>
            Scroll down to see the navbar gradually darken and the second image appear.
          </p>
        </div>
      </section>
    </>
  );
}
