import "bootstrap/dist/css/bootstrap.min.css";
import { useState, useEffect } from "react";
import { useTransition, animated } from "@react-spring/web";
import API from "../api"; // axios instance

export default function Coordinators() {
  const [coordinators, setCoordinators] = useState([]);

  useEffect(() => {
    const fetchCoordinators = async () => {
      try {
        const res = await API.get("/coordinators");
        setCoordinators(res.data);
      } catch (err) {
        console.error("Error fetching coordinators:", err);
      }
    };
    fetchCoordinators();
  }, []);

  const transitions = useTransition(coordinators, {
    from: { opacity: 0, transform: "translateY(20px)" },
    enter: { opacity: 1, transform: "translateY(0px)" },
    leave: { opacity: 0, transform: "translateY(20px)" },
    trail: 100, // staggered effect
  });

  return (
    <div className="container my-5">
      <h2 className="text-center mb-4 fw-bold text-primary">
        Meet Our Coordinators ðŸ‘¥
      </h2>

      <div className="row g-4">
        {transitions((style, c) => (
          <animated.div
            key={c._id}
            style={style}
            className="col-md-6 col-lg-3"
          >
            <div className="bg-white shadow p-4 rounded border border-gray-200">
              {c.image && (
                <img
                  src={`http://localhost:5000${c.image}`}
                  alt={c.name}
                  className="card-img-top img-fluid"
                />
              )}
              <h5 className="mt-3">{c.name}</h5>
              <p className="text-muted">{c.designation}</p>
            </div>
          </animated.div>
        ))}
      </div>
    </div>
  );
}
