import { useState, useEffect } from "react";
import API from "../../api";

export default function AddParticipantForm() {
  const [form, setForm] = useState({
    name: "",
    email: "",
    phone: "",
    college: "",
    department: "",
    year: "",
    social_link: "",
    accommodation_status: "pending",
    travel_status: "pending",
    registration_id: "",
    events: [],
  });

  const [participants, setParticipants] = useState([]);
  const [events, setEvents] = useState([]);
  const [editingId, setEditingId] = useState(null);

  useEffect(() => {
    fetchParticipants();
    fetchEvents();
  }, []);

  // 🧠 Fetch participants
  const fetchParticipants = async () => {
    const res = await API.get("/participants");
    setParticipants(res.data);
  };

  // 🧠 Fetch events list for dropdown
  const fetchEvents = async () => {
    const res = await API.get("/events");
    setEvents(res.data);
  };

  // 🖊️ Handle input
  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  // 🧩 Handle multi-event selection
  const handleEventSelect = (e) => {
    const selected = Array.from(e.target.selectedOptions, (opt) => opt.value);
    setForm({ ...form, events: selected });
  };

  // 💾 Handle Add / Update
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editingId) {
        await API.put(`/participants/${editingId}`, form);
        alert("✅ Participant updated successfully!");
      } else {
        await API.post("/participants", form);
        alert("✅ Participant added successfully!");
      }
      setForm({
        name: "",
        email: "",
        phone: "",
        college: "",
        department: "",
        year: "",
        social_link: "",
        accommodation_status: "pending",
        travel_status: "pending",
        registration_id: "",
        events: [],
      });
      setEditingId(null);
      fetchParticipants();
    } catch (err) {
      alert("❌ Error saving participant");
    }
  };

  // ❌ Delete
  const handleDelete = async (id) => {
    if (window.confirm("Delete this participant?")) {
      await API.delete(`/participants/${id}`);
      alert("🗑️ Deleted successfully!");
      fetchParticipants();
    }
  };

  // ✏️ Edit
  const handleEdit = (p) => {
    setForm({
      ...p,
      events: p.events.map((ev) => ev._id), // convert populated objects → IDs
    });
    setEditingId(p._id);
  };

  return (
    <div className="container my-4">
      <h2 className="fw-bold mb-3">🎓 Manage Participants</h2>

      {/* ➕ Add / Update Form */}
      <form
        onSubmit={handleSubmit}
        className="p-3 border rounded shadow-sm bg-light"
      >
        <div className="row g-3">
          <div className="col-md-6">
            <input
              name="name"
              value={form.name}
              onChange={handleChange}
              placeholder="Full Name"
              className="form-control"
              required
            />
          </div>
          <div className="col-md-6">
            <input
              name="email"
              value={form.email}
              onChange={handleChange}
              placeholder="Email"
              className="form-control"
              required
            />
          </div>
          <div className="col-md-6">
            <input
              name="phone"
              value={form.phone}
              onChange={handleChange}
              placeholder="Phone"
              className="form-control"
            />
          </div>
          <div className="col-md-6">
            <input
              name="college"
              value={form.college}
              onChange={handleChange}
              placeholder="College"
              className="form-control"
            />
          </div>
          <div className="col-md-6">
            <input
              name="department"
              value={form.department}
              onChange={handleChange}
              placeholder="Department"
              className="form-control"
            />
          </div>
          <div className="col-md-6">
            <input
              name="year"
              value={form.year}
              onChange={handleChange}
              placeholder="Year (e.g. 3rd Year)"
              className="form-control"
            />
          </div>

          {/* 🌐 Social link */}
          <div className="col-md-12">
            <input
              name="social_link"
              value={form.social_link}
              onChange={handleChange}
              placeholder="LinkedIn / Instagram link"
              className="form-control"
            />
          </div>

          {/* 🎯 Multi-Event Selector */}
          <div className="col-md-12">
            <label className="form-label fw-semibold">Select Events</label>
            <select
              multiple
              value={form.events}
              onChange={handleEventSelect}
              className="form-select"
            >
              {events.map((ev) => (
                <option key={ev._id} value={ev._id}>
                  {ev.name}
                </option>
              ))}
            </select>
          </div>

          {/* Status dropdowns */}
          <div className="col-md-6">
            <select
              name="accommodation_status"
              value={form.accommodation_status}
              onChange={handleChange}
              className="form-select"
            >
              <option value="pending">Accommodation Pending</option>
              <option value="confirmed">Accommodation Confirmed</option>
              <option value="rejected">Accommodation Rejected</option>
            </select>
          </div>
          <div className="col-md-6">
            <select
              name="travel_status"
              value={form.travel_status}
              onChange={handleChange}
              className="form-select"
            >
              <option value="pending">Travel Pending</option>
              <option value="confirmed">Travel Confirmed</option>
              <option value="rejected">Travel Rejected</option>
            </select>
          </div>
        </div>

        <div className="text-center mt-4">
          <button
            type="submit"
            className={`btn ${
              editingId ? "btn-warning" : "btn-primary"
            } px-5 fw-semibold`}
          >
            {editingId ? "Update Participant" : "Add Participant"}
          </button>
        </div>
      </form>

      {/* 📋 Participants List */}
      <div className="table-responsive">
        <table className="table table-striped table-hover shadow-sm">
          <thead className="table-dark">
            <tr>
              <th>Name</th>
              <th>Email</th>
              <th>College</th>
              <th>Event(s)</th>
              <th>Accommodation</th>
              <th>Travel</th>
              <th>Reg ID</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {participants.map((p) => (
              <tr key={p._id}>
                <td>{p.name}</td>
                <td>{p.email}</td>
                <td>{p.college}</td>
                <td>{p.events.map((ev) => ev.name).join(", ")}</td>
                <td>{p.accommodation_status}</td>
                <td>{p.travel_status}</td>
                <td>{p.registration_id}</td>
                <td>
                  <button
                    onClick={() => handleDelete(p._id)}
                    className="btn btn-sm btn-outline-danger"
                  >
                    🗑️ Delete
                  </button>
                </td>
                <td>
                   <button
                    onClick={() => handleEdit(p)}
                    className="btn btn-sm btn-outline-warning"
                  >
                    ✏️ Edit
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}
