import { useEffect, useState } from "react";
import API from "../api";
import { Carousel } from "react-bootstrap"; // Make sure react-bootstrap is installed

export default function AboutPage() {
  const [about, setAbout] = useState(null);

  useEffect(() => {
    const fetchAbout = async () => {
      try {
        const res = await API.get("/about");
        setAbout(res.data);
      } catch (err) {
        console.error(err);
      }
    };
    fetchAbout();
  }, []);

  if (!about) return <p>Loading...</p>;

  const baseURL = "http://localhost:5000";

  const LazyImage = ({ src, alt, style, className }) => {
    const [loaded, setLoaded] = useState(false);
    const finalSrc = src.startsWith("http") ? src : baseURL + src;

    return (
      <div style={{ backgroundColor: "#e0e0e0", ...style }}>
        <img
          src={finalSrc}
          alt={alt}
          loading="lazy"
          className={className}
          onLoad={() => setLoaded(true)}
          style={{
            width: "100%",
            height: "100%",
            objectFit: "cover",
            opacity: loaded ? 1 : 0,
            transition: "opacity 0.5s ease-in-out",
            display: "block",
          }}
        />
      </div>
    );
  };

  return (
    <div className="container my-5">
      <div
  style={{
    display: "flex",
    flexWrap: "wrap", // mobile साठी stacked
    gap: "20px",
    alignItems: "center",
    marginBottom: "30px",
  }}
>
  {/* Poster */}
  {about.poster && (
    <img
      src={about.poster.startsWith("http") ? about.poster : "http://localhost:5000" + about.poster}
      alt="Poster"
      style={{
        width: "100%",
        maxWidth: "400px",
        height: "auto",
        borderRadius: "8px",
        flexShrink: 0,
      }}
    />
  )}

  {/* Description */}
  <div style={{ flex: 1, minWidth: "250px" }}>
    <p style={{ fontSize: "1rem", lineHeight: "1.6" }}>{about.description}</p>
  </div>
</div>


      {/* Gallery Carousel */}
      {about.gallery.length > 0 && (
        <Carousel className="mb-4" interval={3000} pause="hover">
          {about.gallery.map((img, idx) => (
            <Carousel.Item key={idx}>
              <LazyImage
                src={img}
                alt={`Gallery ${idx}`}
                style={{ width: "100%", height: "400px", borderRadius: "8px" }}
              />
            </Carousel.Item>
          ))}
        </Carousel>
      )}

      {/* Schedule */}
      <h3>Schedule</h3>
      <ul className="list-group mb-4">
        {about.schedule.map((s, idx) => (
          <li key={idx} className="list-group-item">
            {s.date} - {s.event} ({s.time})
          </li>
        ))}
      </ul>

{/* Sponsors */}
<h3 className="mt-4 mb-2">Sponsors</h3>
<div
  style={{
    display: "flex",
    flexWrap: "wrap",
    gap: "24px",
    justifyContent: "center",
    alignItems: "center",
    padding: "10px 0",
  }}
>
  {about.sponsors.map((s, idx) => (
    <div
      key={idx}
      style={{
        flex: "0 0 140px", // slightly wider for logos
        textAlign: "center",
      }}
    >
      <LazyImage
        src={s.logo}
        alt={s.name}
        style={{
         
          borderRadius: "8px",
          objectFit: "contain", // important: preserves aspect ratio
          backgroundColor: "#f0f0f0", // placeholder bg
        }}
      />
      
    </div>
  ))}
</div>


    </div>
  );
}
