import { useState } from "react";
import API from "../api"; // axios instance

export default function AddParticipant() {
  const [form, setForm] = useState({
    name: "",
    email: "",
    phone: "",
    college: "",
    department: "",
    year: "",
    social_link: "",
    events: [],
  });

  // ‚úèÔ∏è Input change
  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  // üíæ Add participant
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await API.post("/participants", form);
      alert("‚úÖ Participant added successfully!");
      setForm({
        name: "",
        email: "",
        phone: "",
        college: "",
        department: "",
        year: "",
        social_link: "",
        events: [],
      });
    } catch (err) {
      console.error(err);
      alert("‚ùå Error adding participant");
    }
  };

  return (
    <div className="container mt-5">
      <div className="card shadow-sm">
        <div className="card-body">
          <h3 className="text-center mb-4">‚ûï Add New Participant</h3>

          <form
            onSubmit={handleSubmit}
            className="d-flex flex-column align-items-center gap-3"
          >
            <input
              type="text"
              className="form-control w-75"
              placeholder="Full Name"
              name="name"
              value={form.name}
              onChange={handleChange}
              required
            />
            <input
              type="email"
              className="form-control w-75"
              placeholder="Email"
              name="email"
              value={form.email}
              onChange={handleChange}
              required
            />
            <input
              type="text"
              className="form-control w-75"
              placeholder="Phone"
              name="phone"
              value={form.phone}
              onChange={handleChange}
            />
            <input
              type="text"
              className="form-control w-75"
              placeholder="College"
              name="college"
              value={form.college}
              onChange={handleChange}
            />
            <input
              type="text"
              className="form-control w-75"
              placeholder="Department"
              name="department"
              value={form.department}
              onChange={handleChange}
            />
            <input
              type="text"
              className="form-control w-75"
              placeholder="Year"
              name="year"
              value={form.year}
              onChange={handleChange}
            />
            <input
              type="url"
              className="form-control w-75"
              placeholder="LinkedIn / Instagram link"
              name="social_link"
              value={form.social_link}
              onChange={handleChange}
            />

            <button type="submit" className="btn btn-success px-5 fw-semibold">
              Add Participant
            </button>
          </form>
        </div>
      </div>
    </div>
  );
}
