import { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import "../css/navbar.css";

export default function Navbar() {
  const [scrolled, setScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      // जरा जरी scroll झाला तरी hero-section गायब
      setScrolled(window.scrollY > 10);
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <>
      {/* 🌆 Second Background (always behind) */}
      <div className={`background-main ${scrolled ? "visible" : ""}`} />

      {/* 🏞️ Hero Section (visible only at top) */}
      <header className={`hero-section ${scrolled ? "fade-out" : "fade-in"}`}>
        <div className="overlay-text">
          <h1 className="display-3 fw-bold text-white glow">GenVision 2025</h1>
          <p className="text-light fs-4">Innovate. Inspire. Ignite.</p>
        </div>
      </header>

      {/* 🌙 Navbar */}
      <nav
        className={`navbar navbar-expand-lg fixed-top ${
          scrolled ? "scrolled-nav" : "transparent-nav"
        }`}
      >
        <div className="container">
          <Link className="navbar-brand fw-bold text-white" to="/">
            GenVision
          </Link>

          <button
            className="navbar-toggler bg-light"
            type="button"
            data-bs-toggle="collapse"
            data-bs-target="#navbarNav"
          >
            <span className="navbar-toggler-icon"></span>
          </button>

          <div className="collapse navbar-collapse justify-content-end" id="navbarNav">
            <ul className="navbar-nav text-center">
              <li className="nav-item"><Link className="nav-link text-white" to="/">About</Link></li>
              <li className="nav-item"><Link className="nav-link text-white" to="/Events">Events</Link></li>
              <li className="nav-item"><Link className="nav-link text-white" to="/Participants">Participate</Link></li>
              <li className="nav-item"><Link className="nav-link text-white" to="/Coordinators">Coordinators</Link></li>
              <li className="nav-item"><Link className="nav-link text-white" to="/Guests">Guests</Link></li>
            </ul>
          </div>
        </div>
      </nav>
    </>
  );
}
