import { useState } from "react";
import API from "../../api";

export default function AddParticipantForm() {
  const [form, setForm] = useState({
    name: "",
    college: "",
    education: "",
    social_link: "",
    accommodation_status: "pending",
    travel_status: "pending",
    registration_id: "",
  });

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    await API.post("/participants", form);
    alert("✅ Participant added successfully!");
    setForm({
      name: "",
      college: "",
      education: "",
      social_link: "",
      accommodation_status: "pending",
      travel_status: "pending",
      registration_id: "",
    });
  };

  return (
    <div>
      <h2 className="text-2xl font-bold mb-4">Add New Participant</h2>
      <form onSubmit={handleSubmit} className="space-y-3">
        <input
          name="name"
          value={form.name}
          onChange={handleChange}
          placeholder="Name"
          className="w-full p-2 border rounded"
        />
        <input
          name="college"
          value={form.college}
          onChange={handleChange}
          placeholder="College"
          className="w-full p-2 border rounded"
        />
        <input
          name="education"
          value={form.education}
          onChange={handleChange}
          placeholder="Education"
          className="w-full p-2 border rounded"
        />
        <input
          name="social_link"
          value={form.social_link}
          onChange={handleChange}
          placeholder="Social Media Link"
          className="w-full p-2 border rounded"
        />
        <input
          name="registration_id"
          value={form.registration_id}
          onChange={handleChange}
          placeholder="Registration ID"
          className="w-full p-2 border rounded"
        />

        <div className="flex gap-4">
          <select
            name="accommodation_status"
            value={form.accommodation_status}
            onChange={handleChange}
            className="p-2 border rounded"
          >
            <option value="pending">Accommodation Pending</option>
            <option value="confirmed">Accommodation Confirmed</option>
            <option value="rejected">Accommodation Rejected</option>
          </select>
          <select
            name="travel_status"
            value={form.travel_status}
            onChange={handleChange}
            className="p-2 border rounded"
          >
            <option value="pending">Travel Pending</option>
            <option value="confirmed">Travel Confirmed</option>
            <option value="rejected">Travel Rejected</option>
          </select>
        </div>

        <button
          type="submit"
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
        >
          Add Participant
        </button>
      </form>
    </div>
  );
}
