import { useState, useEffect } from "react";
import API from "../../api";

export default function AddEventForm() {
  const [form, setForm] = useState({ name: "", description: "", image: "" });
  const [events, setEvents] = useState([]);
  const [editingId, setEditingId] = useState(null);

  // 🧩 Fetch all events when component loads
  useEffect(() => {
    fetchEvents();
  }, []);

  const fetchEvents = async () => {
    const res = await API.get("/events");
    setEvents(res.data);
  };

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  // 🧩 Add new event
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (editingId) {
      await API.put(`/events/${editingId}`, form);
      alert("✅ Event updated successfully!");
    } else {
      await API.post("/events", form);
      alert("✅ Event added successfully!");
    }
    setForm({ name: "", description: "", image: "" });
    setEditingId(null);
    fetchEvents();
  };

  // 🧩 Delete event
  const handleDelete = async (id) => {
    if (window.confirm("Are you sure you want to delete this event?")) {
      await API.delete(`/events/${id}`);
      alert("🗑️ Event deleted!");
      fetchEvents();
    }
  };

  // 🧩 Edit event (load data into form)
  const handleEdit = (event) => {
    setForm({
      name: event.name,
      description: event.description,
      image: event.image,
    });
    setEditingId(event._id);
  };

  return (
    <div className="card shadow-sm border-0 h-100">
      <h2 className="text-2xl font-bold">Manage Events</h2>

      {/* ✏️ Add/Edit Form */}
      <form onSubmit={handleSubmit} className="d-flex flex-column align-items-center gap-3">
        <input
          name="name"
          value={form.name}
          onChange={handleChange}
          placeholder="Event Name"
          className="w-full p-2 border rounded"
        />
        <textarea
          name="description"
          value={form.description}
          onChange={handleChange}
          placeholder="Event Description"
          className="w-screen p-4 border rounded"
        />
        <input
          name="image"
          value={form.image}
          onChange={handleChange}
          placeholder="Image URL"
          className="w-full p-2 border rounded"
        />
        <button
          type="submit"
          className={`px-4 py-2 rounded text-white ${
            editingId ? "bg-yellow-500 hover:bg-yellow-600" : "bg-blue-600 hover:bg-blue-700"
          }`}
        >
          {editingId ? "Update Event" : "Add Event"}
        </button>
      </form>

      {/* 📋 Events List */}
      <div className="mt-6">
        <h3 className="text-xl font-semibold mb-3">Existing Events</h3>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          {events.map((event) => (
            <div
              key={event._id}
              className="bg-white p-4 shadow rounded border border-gray-200"
            >
              <img
                src={event.image}
                alt={event.name}
                className="w-full h-40 object-cover rounded mb-3"
              />
              <h4 className="text-lg font-semibold">{event.name}</h4>
              <p className="text-gray-600 text-sm mb-3">
                {event.description?.slice(0, 100)}...
              </p>
              <div className="flex gap-2">
                <button
                  onClick={() => handleEdit(event)}
                  className="bg-yellow-400 hover:bg-yellow-500 px-3 py-1 rounded"
                >
                  ✏️ Edit
                </button>
                <button
                  onClick={() => handleDelete(event._id)}
                  className="bg-red-600 hover:bg-red-700 text-white px-3 py-1 rounded"
                >
                  🗑️ Delete
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
