import { useEffect, useState } from "react";
import axios from "axios";

export default function Events() {
  const [events, setEvents] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:5000/api/events")
         .then(res => setEvents(res.data))
         .catch(err => console.log(err));
  }, []);

  return (
    <div className="p-8">
      <h2 className="text-2xl font-bold mb-4">ðŸŽ¯ GenVision Events</h2>
      <div className="grid md:grid-cols-3 gap-4">
        {events.map((e) => (
          <div key={e._id} className="p-4 border rounded-lg shadow">
            <img src={e.image} alt={e.title} className="w-full h-40 object-cover rounded" />
            <h3 className="text-xl mt-2 font-semibold">{e.title}</h3>
            <p className="text-sm text-gray-600">{e.description}</p>
            <p className="text-xs text-gray-400 mt-1">{e.date}</p>
          </div>
        ))}
      </div>
    </div>
  );
}
