import { useState, useEffect } from "react";
import API from "../../api";

export default function AddEventForm() {
  const [form, setForm] = useState({ name: "", description: "", date:"", venue:""});
  const [imageFile, setImageFile] = useState(null);
  const [events, setEvents] = useState([]);
  const [editingId, setEditingId] = useState(null);

  // 🧩 Fetch all events when component loads
  useEffect(() => {
    fetchEvents();
  }, []);

  const fetchEvents = async () => {
    try {
          const res = await API.get("/events");
          console.log("Events fetched:", res.data);
          setEvents(res.data);
        } catch (err) {
          console.error(err);
        }
  };

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  // 🧩 Add new event
  const handleSubmit = async (e) => {
  e.preventDefault();
const formData = new FormData();
Object.keys(form).forEach(key => formData.append(key, form[key]));
if (imageFile) formData.append("image", imageFile);
formData.append("name", form.name);
formData.append("date", form.date);
formData.append("venue", form.venue);
formData.append("description", form.description);
if (editingId) {
  
    await API.put(`/events/${editingId}`, formData, {
    headers: { "Content-Type": "multipart/form-data" },
  });
  
  
}else {
  try {
  await API.post("/events", formData, {
    headers: { "Content-Type": "multipart/form-data" },
  });
  } catch (err) {
      console.error("Error submitting form:", err);
      alert("❌ Error adding/updating coordinator");
  }
}

    setForm({ name: "", description: "", date: "", venue: ""});
    setImageFile(null);
    setEditingId(null);
    fetchEvents();
};

  // 🧩 Delete event
  const handleDelete = async (id) => {
    if (window.confirm("Are you sure you want to delete this event?")) {
      await API.delete(`/events/${id}`);
      alert("🗑️ Event deleted!");
      fetchEvents();
    }
  };

  // 🧩 Edit event (load data into form)
const handleEdit = async (req, res) => {
  try {
    const updateData = { ...req.body };
    if (req.file) updateData.image = `/uploads/${req.file.filename}`;
    const updatedEvent = await Event.findByIdAndUpdate(req.params.id, updateData, { new: true });
    if (!updatedEvent) return res.status(404).json({ message: "Event not found" });
    res.json(updatedEvent);
  } catch (err) {
    res.status(400).json({ message: err.message });
  }
};

  return (
    <div className="card shadow-sm border-0 h-100">
      <h2 className="text-2xl font-bold">Manage Events</h2>

      {/* ✏️ Add/Edit Form */}
      <form onSubmit={handleSubmit} className="d-flex flex-column align-items-center gap-3">
        <input
          name="name"
          value={form.name}
          onChange={handleChange}
          placeholder="Event Name"
          className="col-md-6 p-2 border rounded"
        />
        <textarea
          name="description"
          value={form.description}
          onChange={handleChange}
          placeholder="Event Description"
          className="col-md-6 p-4 border rounded"
        />
             <input
          type="date"
          name="date"
          value={form.date}
          onChange={handleChange}
          className="col-md-6 p-2 border rounded"
          required
        />
        <input
          type="time"
          name="time"
          value={form.time}
          onChange={handleChange}
          className="col-md-6 p-2 border rounded"
        />
        <input
          name="venue"
          value={form.venue}
          onChange={handleChange}
          placeholder="Venue"
          className="col-md-6 p-2 border rounded"
          required
        />
        <div className="col-md-2">
          <label className="form-label">Image</label>
          <input
            type="file"
            onChange={(e) => setImageFile(e.target.files[0])}
            className="form-control"
          />
          {imageFile && (
            <img
              src={URL.createObjectURL(imageFile)}
              alt="Preview"
              className="img-thumbnail mt-2"
              style={{ width: "150px", height: "200px", objectFit: "cover" }}
            />
          )}
        </div>
        <button
          type="submit"
          className={`px-4 py-2 rounded text-white ${
            editingId ? "bg-yellow-500 hover:bg-yellow-600" : "bg-blue-600 hover:bg-blue-700"
          }`}
        >
          {editingId ? "Update Event" : "Add Event"}
        </button>
      </form>

      {/* 📋 Events List */}
      <div className="mt-6">
        <h3 className="text-xl font-semibold mb-3">Existing Events</h3>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          {events.map((event) => (
            <div
              key={event._id}
              className="bg-white p-4 shadow rounded border border-gray-200"
            >
            
             {event.image && (
                    <img
    src={`http://localhost:5000${event.image}`}
    alt={event.name}
    style={{ height: "200px", objectFit: "cover" }}
  />
)}
            
              <h4 className="text-lg font-semibold">{event.name}</h4>
              <p className="text-gray-600 text-sm mb-3">
                {event.description?.slice(0, 100)}...
              </p>
              <div className="flex gap-2">
                <button
                  onClick={() => handleEdit(event)}
                  className="bg-yellow-400 hover:bg-yellow-500 px-3 py-1 rounded"
                >
                  ✏️ Edit
                </button>
                <button
                  onClick={() => handleDelete(event._id)}
                  className="bg-red-600 hover:bg-red-700 text-white px-3 py-1 rounded"
                >
                  🗑️ Delete
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
