import { useEffect, useState } from "react";
import API from "../api";
import { Carousel } from "react-bootstrap"; // Make sure react-bootstrap is installed

export default function AboutPage() {
  const [about, setAbout] = useState(null);
  const [flipped, setFlipped] = useState(false);

  useEffect(() => {
    const fetchAbout = async () => {
      try {
        const res = await API.get("/about");
        setAbout(res.data);
      } catch (err) {
        console.error(err);
      }
    };
    fetchAbout();
  }, []);

  if (!about) return <p>Loading...</p>;

  const baseURL = "http://localhost:5000";

  const LazyImage = ({ src, alt, style, className }) => {
    const [loaded, setLoaded] = useState(false);
    const finalSrc = src.startsWith("http") ? src : baseURL + src;

    return (
      <div style={{ backgroundColor: "#e0e0e0", ...style }}>
        <img
          src={finalSrc}
          alt={alt}
          loading="lazy"
          className={className}
          onLoad={() => setLoaded(true)}
          style={{
            width: "100%",
            height: "100%",
            objectFit: "cover",
            opacity: loaded ? 1 : 0,
            transition: "opacity 0.5s ease-in-out",
            display: "block",
          }}
        />
      </div>
    );
  };

  return (
    <div className="container my-5">
      <div
  style={{
    display: "flex",
    flexWrap: "wrap", // mobile साठी stacked
    gap: "20px",
    alignItems: "center",
    marginBottom: "30px",
  }}
>
 <div
      style={{
        perspective: "1000px", // flip perspective
        maxWidth: "400px",
        margin: "0 auto",
      }}
    >
      <div
        onClick={() => setFlipped((prev) => !prev)}
        style={{
          width: "100%",
          height: "auto",
          cursor: "pointer",
          position: "relative",
          transformStyle: "preserve-3d",
          transition: "transform 0.6s",
          transform: flipped ? "rotateY(180deg)" : "rotateY(0deg)",
        }}
      >
        {/* Front: Image */}
        <div
          style={{
            backfaceVisibility: "hidden",
            width: "100%",
            borderRadius: "12px",
            overflow: "hidden",
          }}
        >
          <img
            src={
              about.poster.startsWith("http")
                ? about.poster
                : "http://localhost:5000" + about.poster
            }
            alt="Poster"
            style={{
              width: "100%",
              display: "block",
            }}
          />
        </div>

        {/* Back: Description */}
        <div
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            backfaceVisibility: "hidden",
            transform: "rotateY(180deg)",
            backgroundColor: "#f9f9f9",
            padding: "20px",
            borderRadius: "12px",
            boxShadow: "0 4px 12px rgba(0,0,0,0.1)",
          }}
        >
          <p
            style={{
              fontSize: "1rem",
              lineHeight: "1.8",
              color: "#333",
              textAlign: "justify",
              margin: 0,
            }}
          >
            {about.description}
          </p>
        </div>
      </div>
    </div>
</div>



      {/* Gallery Carousel */}
      {about.gallery.length > 0 && (
        <Carousel className="mb-4" interval={3000} pause="hover" style={{ borderRadius: "20px", overflow: "hidden" }}>
  {about.gallery.map((img, idx) => (
    <Carousel.Item key={idx}>
      <LazyImage
        src={img}
        alt={`Gallery ${idx}`}
        style={{
          width: "100%",
          height: "500px",
          objectFit: "cover",
          borderRadius: "20px",
        }}
      />
    </Carousel.Item>
  ))}
</Carousel>

      )}

      {/* Schedule */}
      <h3>Schedule</h3>
      <ul className="list-group mb-4">
        {about.schedule.map((s, idx) => (
          <li key={idx} className="list-group-item">
            {s.date} - {s.event} ({s.time})
          </li>
        ))}
      </ul>

{/* Sponsors */}
<h3 className="mt-4 mb-2">Sponsors</h3>
<div
  style={{
    display: "flex",
    flexWrap: "wrap",
    gap: "24px",
    justifyContent: "center",
    alignItems: "center",
    padding: "10px 0",
  }}
>
  {about.sponsors.map((s, idx) => (
    <div
      key={idx}
      style={{
        flex: "0 0 140px", // slightly wider for logos
        textAlign: "center",
      }}
    >
      <LazyImage
        src={s.logo}
        alt={s.name}
        style={{
         
          borderRadius: "8px",
          objectFit: "contain", // important: preserves aspect ratio
          backgroundColor: "#f0f0f0", // placeholder bg
        }}
      />
      
    </div>
  ))}
</div>


    </div>
  );
}
