import { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import "./Navbar.css";

export default function Navbar() {
  const [scrolled, setScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setScrolled(window.scrollY > window.innerHeight - 100);
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <>
      <div className="background-image" /> {/* ðŸ”¥ persistent background */}

      <header className="hero-section d-flex align-items-center justify-content-center text-center">
        <div className="overlay-text">
          <h1 className="display-3 fw-bold text-white glow">GenVision 2025</h1>
          <p className="text-light fs-4">Innovate. Inspire. Ignite.</p>
        </div>
      </header>

      <nav
        className={`navbar navbar-expand-lg navbar-dark fixed-top ${
          scrolled ? "scrolled-nav" : "transparent-nav"
        }`}
      >
        <div className="container">
          <Link className="navbar-brand fw-bold" to="/">
            GenVision
          </Link>
          <button
            className="navbar-toggler"
            type="button"
            data-bs-toggle="collapse"
            data-bs-target="#navbarNav"
          >
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse justify-content-end" id="navbarNav">
            <ul className="navbar-nav text-center">
              <li className="nav-item">
                <Link className="nav-link" to="/">About</Link>
              </li>
              <li className="nav-item">
                <Link className="nav-link" to="/Events">Events</Link>
              </li>
              <li className="nav-item">
                <Link className="nav-link" to="/Participants">Participate</Link>
              </li>
              <li className="nav-item">
                <Link className="nav-link" to="/Coordinators">Coordinators</Link>
              </li>
              <li className="nav-item">
                <Link className="nav-link" to="/Guests">Guests</Link>
              </li>
            </ul>
          </div>
        </div>
      </nav>
    </>
  );
}
