import "bootstrap/dist/css/bootstrap.min.css";
import coordinators from "./Coordinators"
export default function Coordinators() {
  return (
    <div className="container my-5">
      <h2 className="text-center mb-4 fw-bold text-primary">
        Meet Our Coordinators ðŸ‘¥
      </h2>

      {/* Coordinator Cards */}
     <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-4 mt-5">
        {coordinators.map((c) => (
          <div
            key={c._id}
            className="bg-white shadow p-4 rounded border border-gray-200"
          >
            {c.image && (
              <img
                src={c.image}
                alt={c.name}
                className="w-full h-40 object-cover rounded mb-2"
              />
            )}
            <h4 className="text-lg font-semibold">{c.name}</h4>
            <p className="text-gray-600">{c.designation}</p>
            <p className="text-gray-500 text-sm mt-1">{c.contact}</p>
          </div>
        ))}
      </div>

      {/* Add Coordinator Form */}
      
    </div>
  );
}
