import { useEffect } from "react";
import { Link } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import "../css/navbar.css"; // CSS वेगळी ठेवली आहे clarity साठी

export default function Navbar() {


useEffect(() => {
  const handleScroll = () => {
    const navbar = document.querySelector(".navbar");
    if (window.scrollY > 50) navbar.classList.add("scrolled");
    else navbar.classList.remove("scrolled");
  };
  window.addEventListener("scroll", handleScroll);
  return () => window.removeEventListener("scroll", handleScroll);
}, []);

  return (
    <>
      {/* Fullscreen Background Section */}
      <section className="hero-section">
        <div className="overlay">
          <h1 className="hero-text">Welcome to GenVision 2025</h1>
        </div>
      </section>

      {/* Fixed Navbar */}
      <nav className="navbar navbar-expand-lg navbar-dark bg-transparent fixed-top">
        <div className="container">
          <h1 className="navbar-brand fw-bold">GenVision 2025</h1>
          <button
            className="navbar-toggler"
            type="button"
            data-bs-toggle="collapse"
            data-bs-target="#navbarNav"
            aria-controls="navbarNav"
            aria-expanded="false"
            aria-label="Toggle navigation"
          >
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse justify-content-end" id="navbarNav">
            <ul className="navbar-nav">
              <li className="nav-item">
                <Link to="/" className="nav-link">About</Link>
              </li>
              <li className="nav-item">
                <Link to="/Events" className="nav-link">Events</Link>
              </li>
              <li className="nav-item">
                <Link to="/Participants" className="nav-link">Participate</Link>
              </li>
              <li className="nav-item">
                <Link to="/Coordinators" className="nav-link">Coordinators</Link>
              </li>
              <li className="nav-item">
                <Link to="/Guests" className="nav-link">Guests</Link>
              </li>
            </ul>
          </div>
        </div>
      </nav>

      {/* Rest of the page content */}
      <section className="content-section">
        <div className="container py-5 text-white">
          <h2>About GenVision</h2>
          <p>
            This is the section where all your event details, coordinators, and guests
            will appear. Scroll effect gives a smooth reveal experience.
          </p>
        </div>
      </section>
    </>
  );
}
