import { useEffect, useState } from "react";
import API from "../api";

export default function Guests() {
  const [guests, setGuests] = useState([]);

  useEffect(() => {
    API.get("/guests")
      .then((res) => setGuests(res.data))
      .catch((err) => console.log(err));
  }, []);

  return (
    <div className="p-8">
      <h1 className="text-3xl font-bold mb-6 text-center text-blue-700">
        ðŸŽ¤ Special Guests
      </h1>

      {guests.length === 0 ? (
        <p className="text-center text-gray-500">No guests added yet...</p>
      ) : (
        <div className="grid md:grid-cols-3 sm:grid-cols-2 grid-cols-1 gap-6">
          {guests.map((g) => (
            <div
              key={g._id}
              className="border rounded-2xl p-4 bg-white shadow-md hover:shadow-lg transition-all duration-300"
            >
              <img
                src={g.image || "https://via.placeholder.com/150"}
                alt={g.name}
                className="w-full h-48 object-cover rounded-xl"
              />
              <h2 className="text-xl font-semibold mt-3 text-gray-800">
                {g.name}
              </h2>
              <p className="text-sm text-gray-600">{g.designation}</p>
              {g.description && (
                <p className="text-gray-500 text-sm mt-2">{g.description}</p>
              )}
              {g.contact && (
                <p className="text-gray-400 text-xs mt-2">
                  ðŸ“ž Contact: {g.contact}
                </p>
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
