import { useEffect, useState } from "react";
import API from "../api";

export default function AboutPage() {
  const [about, setAbout] = useState(null);

  useEffect(() => {
    const fetchAbout = async () => {
      try {
        const res = await API.get("/about");
        setAbout(res.data);
      } catch (err) {
        console.error(err);
      }
    };
    fetchAbout();
  }, []);

  if (!about) return <p>Loading...</p>;

  const baseURL = "http://localhost:5000";

  // LazyImage component
  const LazyImage = ({ src, alt, className, style }) => {
    const [loaded, setLoaded] = useState(false);
    const finalSrc = src.startsWith("http") ? src : baseURL + src;

    return (
      <div
        style={{
          position: "relative",
          backgroundColor: "#e0e0e0",
          overflow: "hidden",
          ...style,
        }}
      >
        <img
          src={finalSrc}
          alt={alt}
          className={className}
          loading="lazy"
          style={{
            opacity: loaded ? 1 : 0,
            transition: "opacity 0.5s ease-in-out",
            width: "100%",
            height: "100%",
            objectFit: "cover",
            position: "absolute",
            top: 0,
            left: 0,
          }}
          onLoad={() => setLoaded(true)}
        />
        {!loaded && <div style={{ width: "100%", height: "100%" }} />}
      </div>
    );
  };

  return (
    <div className="container my-5">
      {/* Poster */}
      {about.poster && (
        <LazyImage
          src={about.poster}
          alt="Poster"
          className="rounded mb-4"
          style={{ width: "100%", height: "400px" }}
        />
      )}

      {/* Description */}
      <p className="mb-4">{about.description}</p>

      {/* Gallery */}
      <div className="row g-3 mb-4">
        {about.gallery.map((img, idx) => (
          <div key={idx} className="col-md-4">
            <LazyImage
              src={img}
              alt={`Gallery ${idx}`}
              className="rounded"
              style={{ width: "100%", height: "200px" }}
            />
          </div>
        ))}
      </div>

      {/* Schedule */}
      <h3>Schedule</h3>
      <ul className="list-group mb-4">
        {about.schedule.map((s, idx) => (
          <li key={idx} className="list-group-item">
            {s.date} - {s.event} ({s.time})
          </li>
        ))}
      </ul>

      {/* Sponsors */}
      <h3>Sponsors</h3>
      <div className="d-flex flex-wrap gap-3 mb-5">
        {about.sponsors.map((s, idx) => (
          <div key={idx} className="text-center">
            <LazyImage
              src={s.logo}
              alt={s.name}
              style={{ width: "120px", height: "80px" }}
            />
            <p className="small mt-1">{s.name}</p>
          </div>
        ))}
      </div>
    </div>
  );
}
