import { useState, useEffect } from "react";
import API from "../../api";

export default function AddCoordinatorForm() {
  const [form, setForm] = useState({
    name: "",
    designation: "",
    contact: "",
    image: "",
  });
  const [coordinators, setCoordinators] = useState([]);
  const [editingId, setEditingId] = useState(null);

  useEffect(() => {
    fetchCoordinators();
  }, []);

  const fetchCoordinators = async () => {
    const res = await API.get("/coordinators");
    setCoordinators(res.data);
  };

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (editingId) {
      await API.put(`/coordinators/${editingId}`, form);
      alert("‚úÖ Coordinator updated successfully!");
    } else {
      await API.post("/coordinators", form);
      alert("‚úÖ Coordinator added successfully!");
    }
    setForm({
      name: "",
      designation: "",
      contact: "",
      image: "",
    });
    setEditingId(null);
    fetchCoordinators();
  };

  const handleDelete = async (id) => {
    if (window.confirm("Delete this coordinator?")) {
      await API.delete(`/coordinators/${id}`);
      alert("üóëÔ∏è Coordinator deleted!");
      fetchCoordinators();
    }
  };

  const handleEdit = (c) => {
    setForm(c);
    setEditingId(c._id);
  };

  return (
    <div className="space-y-6">
      <h2 className="text-2xl font-bold">Manage Coordinators</h2>

      {/* ‚úèÔ∏è Add/Edit Form */}
       <div className="card mt-5 shadow-sm border-0">
        <div className="card-body">
          <h4 className="mb-3 text-center">Add New Coordinator</h4>
          <form
            onSubmit={handleAdd}
            className="d-flex flex-column align-items-center gap-3"
          >
            <input
              type="text"
              className="form-control w-75"
              placeholder="Full Name"
              name="name"
              value={newCoord.name}
              onChange={handleChange}
            />
            <input
              type="text"
              className="form-control w-75"
              placeholder="Role (e.g., Event Lead)"
              name="role"
              value={newCoord.role}
              onChange={handleChange}
            />
            <input
              type="email"
              className="form-control w-75"
              placeholder="Email"
              name="email"
              value={newCoord.email}
              onChange={handleChange}
            />
            <button type="submit" className="btn btn-success px-4">
              Add Coordinator
            </button>
          </form>
        </div>
      </div>
      {/* üìã Coordinator list */}
      <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-4 mt-5">
        {coordinators.map((c) => (
          <div
            key={c._id}
            className="bg-white shadow p-4 rounded border border-gray-200"
          >
            {c.image && (
              <img
                src={c.image}
                alt={c.name}
                className="w-full h-40 object-cover rounded mb-2"
              />
            )}
            <h4 className="text-lg font-semibold">{c.name}</h4>
            <p className="text-gray-600">{c.designation}</p>
            <p className="text-gray-500 text-sm mt-1">{c.contact}</p>
            <div className="flex gap-2 mt-2">
              <button
                onClick={() => handleEdit(c)}
                className="bg-yellow-400 hover:bg-yellow-500 px-3 py-1 rounded"
              >
                ‚úèÔ∏è Edit
              </button>
              <button
                onClick={() => handleDelete(c._id)}
                className="bg-red-600 hover:bg-red-700 text-white px-3 py-1 rounded"
              >
                üóëÔ∏è Delete
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
