import { useEffect, useState } from "react";
import API from "../api";

export default function AboutPage() {
  const [about, setAbout] = useState(null);

  useEffect(() => {
    const fetchAbout = async () => {
      try {
        const res = await API.get("/about");
        setAbout(res.data);
      } catch (err) {
        console.error(err);
      }
    };
    fetchAbout();
  }, []);

  if (!about) return <p>Loading...</p>;

  const baseURL = "http://localhost:5000";

  // LazyImage component
  const LazyImage = ({ src, alt, style, className }) => {
    const [loaded, setLoaded] = useState(false);
    const finalSrc = src.startsWith("http") ? src : baseURL + src;

    return (
      <div
        style={{
          backgroundColor: "#e0e0e0",
          position: "relative",
          ...style,
        }}
      >
        <img
          src={finalSrc}
          alt={alt}
          loading="lazy"
          className={className}
          onLoad={() => setLoaded(true)}
          style={{
            width: "100%",
            height: "100%",
            objectFit: "cover",
            opacity: loaded ? 1 : 0,
            transition: "opacity 0.5s ease-in-out",
            display: "block",
          }}
        />
      </div>
    );
  };

  return (
    <div className="container my-5">
      {/* Poster */}
      {about.poster && (
        <LazyImage
          src={about.poster}
          alt="Poster"
          style={{ width: "100%", height: "400px", borderRadius: "8px", marginBottom: "20px" }}
        />
      )}

      {/* Description */}
      <p className="mb-4">{about.description}</p>

      {/* Gallery */}
      <div className="row g-3 mb-4">
        {about.gallery.map((img, idx) => (
          <div key={idx} className="col-md-4">
            <LazyImage
              src={img}
              alt={`Gallery ${idx}`}
              style={{ width: "100%", height: "200px", borderRadius: "8px" }}
            />
          </div>
        ))}
      </div>

      {/* Schedule */}
      <h3>Schedule</h3>
      <ul className="list-group mb-4">
        {about.schedule.map((s, idx) => (
          <li key={idx} className="list-group-item">
            {s.date} - {s.event} ({s.time})
          </li>
        ))}
      </ul>

      {/* Sponsors */}
      <h3>Sponsors</h3>
      <div className="d-flex flex-wrap gap-3 mb-5">
        {about.sponsors.map((s, idx) => (
          <div key={idx} className="text-center">
            <LazyImage
              src={s.logo}
              alt={s.name}
              style={{ width: "120px", height: "80px", borderRadius: "8px" }}
            />
            <p className="small mt-1">{s.name}</p>
          </div>
        ))}
      </div>
    </div>
  );
}
