import { useEffect, useState } from "react";
import API from "../../api";

export default function EditAbout() {
  const [form, setForm] = useState({
    poster: "",
    description: "",
    gallery: [],
    schedule: [{ date: "", event: "", time: "" }],
    sponsors: [{ name: "", logo: "" }],
  });

  const [posterFile, setPosterFile] = useState(null);
  const [galleryFiles, setGalleryFiles] = useState([]);
  // const [sponsors, setSponsors] = useState([]); // existing logos
  const [newLogos, setNewLogos] = useState([]); // for preview

  


  useEffect(() => {
    const fetchAbout = async () => {
      try {
        const res = await API.get("/about");
        setForm(res.data);
      } catch (err) {
        console.error("Error fetching about:", err);
      }
    };
    fetchAbout();
  }, []);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  // üìÖ Schedule Handlers
  const handleScheduleChange = (index, field, value) => {
    const newSchedule = [...form.schedule];
    newSchedule[index][field] = value;
    setForm({ ...form, schedule: newSchedule });
  };

  const addSchedule = () =>
    setForm({
      ...form,
      schedule: [...form.schedule, { date: "", event: "", time: "" }],
    });

  const removeSchedule = (index) => {
    const newSchedule = form.schedule.filter((_, i) => i !== index);
    setForm({ ...form, schedule: newSchedule });
  };

  // üíº Sponsor Handlers
  const handleSponsorChange = (index, field, value) => {
    const newSponsors = [...form.sponsors];
    newSponsors[index][field] = value;
    setForm({ ...form, sponsors: newSponsors });
  };

  const addSponsor = () =>
    setForm({
      ...form,
      sponsors: [...form.sponsors, { name: "", logo: "" }],
    });

  const removeSponsor = (index) => {
    const newSponsors = form.sponsors.filter((_, i) => i !== index);
    setForm({ ...form, sponsors: newSponsors });
  };

  const handleLogoChange = (e) => {
    const files = Array.from(e.target.files);
    const previews = files.map((file) => ({
      file,
      preview: URL.createObjectURL(file),
    }));
    setNewLogos(previews);}

  

  // üñºÔ∏è Gallery Handlers
  const handleAddGalleryImages = (e) => {
    const files = Array.from(e.target.files);
    setGalleryFiles((prev) => [...prev, ...files]);

    // Temporary preview URLs
    const newPreviews = files.map((file) => URL.createObjectURL(file));
    setForm((prev) => ({
      ...prev,
      gallery: [...prev.gallery, ...newPreviews],
    }));
  };

  const removeGalleryImage = (index) => {
    const newGallery = form.gallery.filter((_, i) => i !== index);
    setForm({ ...form, gallery: newGallery });

    const newGalleryFiles = [...galleryFiles];
    newGalleryFiles.splice(index, 1);
    setGalleryFiles(newGalleryFiles);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const formData = new FormData();
      if (posterFile) formData.append("poster", posterFile);
      galleryFiles.forEach((file) => formData.append("gallery", file));

      // Sponsor images (if any)
      form.sponsors.forEach((s) => {
        if (s.file) formData.append("sponsorFiles", s.file);
      });
      await fetch("http://localhost:5000/api/about/update", {
      method: "PUT",
      body: formData,
    });

      formData.append("description", form.description);
      formData.append("schedule", JSON.stringify(form.schedule));
      formData.append(
        "sponsors",
        JSON.stringify(
          form.sponsors.map((s) => ({
            name: s.name,
            logo: s.logo || "",
          }))
        )
      );

      await API.put("/about", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      alert("‚úÖ About page updated successfully!");
    } catch (err) {
      console.error("Error updating about:", err);
      alert("‚ùå Error updating About Page");
    }
  };

  return (
    <div className="container my-5">
      <h3 className="mb-4 fw-bold text-primary">ü™© Edit About Page</h3>

      <form onSubmit={handleSubmit}>
        {/* üñºÔ∏è Poster */}
        <div className="mb-3">
          <label className="fw-semibold">Poster</label>
          <input
            type="file"
            onChange={(e) => setPosterFile(e.target.files[0])}
            className="form-control"
          />
          {form.poster && (
            <img
              src={`http://localhost:5000${form.poster}`}
              alt="Poster"
              className="mt-3 rounded"
              style={{ width: "300px", objectFit: "cover" }}
            />
          )}
        </div>

        {/* üìù Description */}
        <div className="mb-4">
          <label className="fw-semibold">Description</label>
          <textarea
            name="description"
            value={form.description}
            onChange={handleChange}
            className="form-control"
            rows="4"
          />
        </div>

        {/* üñºÔ∏è Gallery */}
        <h5 className="fw-bold">Gallery</h5>
        <div className="mb-3">
          <input
            type="file"
            multiple
            onChange={handleAddGalleryImages}
            className="form-control"
          />
        </div>

        <div className="d-flex flex-wrap gap-3 mb-4">
          {form.gallery?.map((img, idx) => (
            <div key={idx} className="position-relative">
              <img
                src={
                  img.startsWith("blob:") ? img : `http://localhost:5000${img}`
                }
                alt={`Gallery ${idx}`}
                className="rounded shadow-sm"
                style={{
                  width: "150px",
                  height: "100px",
                  objectFit: "cover",
                }}
              />
              <button
                type="button"
                className="btn btn-sm btn-danger position-absolute top-0 end-0"
                onClick={() => removeGalleryImage(idx)}
              >
                ‚úï
              </button>
            </div>
          ))}
        </div>

        {/* üìÖ Schedule */}
        <h5 className="fw-bold">Schedule</h5>
        {form.schedule.map((s, idx) => (
          <div key={idx} className="d-flex gap-2 mb-2">
            <input
              type="date"
              value={s.date}
              onChange={(e) =>
                handleScheduleChange(idx, "date", e.target.value)
              }
              className="form-control"
            />
            <input
              type="text"
              placeholder="Event Name"
              value={s.event}
              onChange={(e) =>
                handleScheduleChange(idx, "event", e.target.value)
              }
              className="form-control"
            />
            <input
              type="time"
              value={s.time}
              onChange={(e) =>
                handleScheduleChange(idx, "time", e.target.value)
              }
              className="form-control"
            />
            <button
              type="button"
              onClick={() => removeSchedule(idx)}
              className="btn btn-danger"
            >
              ‚ùå
            </button>
          </div>
        ))}
        <button
          type="button"
          onClick={addSchedule}
          className="btn btn-outline-primary mb-4"
        >
          + Add Schedule
        </button>

        {/* üíº Sponsors */}
        <h5 className="fw-bold">Sponsors</h5>
        {form.sponsors.map((s, idx) => (
          <div key={idx} className="d-flex gap-2 mb-2 align-items-center">
            <input
              type="text"
              placeholder="Sponsor Name"
              value={s.name}
              onChange={(e) =>
                handleSponsorChange(idx, "name", e.target.value)
              }
              className="form-control"
            />
            <input
              type="file"
              onChange={(e) => {
                const newSponsors = [...form.sponsors];
                newSponsors[idx].file = e.target.files[0];
                setForm({ ...form, sponsors: newSponsors });
                handleLogoChange();

              }}
              className="form-control"
            />
             <div className="flex flex-wrap gap-4 mb-4">
        {newLogos.map((logo, index) => (
          <div key={index} className="w-32 h-32 border rounded overflow-hidden">
            <img
              src={logo.preview}
              alt={`Sponsor ${index}`}
              className="w-full h-full object-cover"
            />
          </div>
        ))}
      </div>
            <button
              type="button"
              onClick={() => removeSponsor(idx)}
              className="btn btn-danger"
            >
              ‚ùå
            </button>
          </div>
        ))}
        <button
          type="button"
          onClick={addSponsor}
          className="btn btn-outline-primary mb-4"
        >
          + Add Sponsor
        </button>

        <div className="text-center">
          <button type="submit" className="btn btn-success px-5 fw-semibold">
            üíæ Save Changes
          </button>
        </div>
      </form>
    </div>
  );
}
