import { useEffect, useState } from "react";
import API from "../api";

export default function Events() {
  const [events, setEvents] = useState([]);

  useEffect(() => {
    API.get("/events")
      .then((res) => setEvents(res.data))
      .catch((err) => console.log(err));
  }, []);

  return (
    <div className="p-8">
      <h1 className="text-3xl font-bold mb-4">ðŸŽª GenVision Events</h1>
      <div className="grid md:grid-cols-3 gap-4">
        {events.map((e) => (
          <div key={e._id} className="border p-4 rounded shadow-sm bg-white">
          {e.image && (
            <img
  src={`http://localhost:5000${e.image}`}
  alt={e.name}
  className="card-img-top img-fluid "
  // style={{height: "500px"}}
/>
              )}
            <h2 className="text-xl mt-2 font-semibold">{e.name}</h2>
            <p className="text-gray-700 text-sm">{e.description}</p>
          </div>
        ))}
      </div>
    </div>
  );
}
