import { useEffect, useState } from "react";
import API from "../../api"; // axios instance

export default function EditAbout() {
  const [form, setForm] = useState({
    poster: "",
    description: "",
    gallery: [""],
    schedule: [{ date: "", event: "", time: "" }],
    sponsors: [{ name: "", logo: "" }],
  });

  useEffect(() => {
    const fetchAbout = async () => {
      try {
        const res = await API.get("/about");
        setForm(res.data);
      } catch (err) {
        console.error(err);
      }
    };
    fetchAbout();
  }, []);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  // 🔹 Gallery
  const handleGalleryChange = (index, value) => {
    const newGallery = [...form.gallery];
    newGallery[index] = value;
    setForm({ ...form, gallery: newGallery });
  };
  const addGallery = () => setForm({ ...form, gallery: [...form.gallery, ""] });
  const removeGallery = (index) => {
    const newGallery = form.gallery.filter((_, i) => i !== index);
    setForm({ ...form, gallery: newGallery });
  };

  // 🔹 Schedule
  const handleScheduleChange = (index, field, value) => {
    const newSchedule = [...form.schedule];
    newSchedule[index][field] = value;
    setForm({ ...form, schedule: newSchedule });
  };
  const addSchedule = () =>
    setForm({ ...form, schedule: [...form.schedule, { date: "", event: "", time: "" }] });
  const removeSchedule = (index) => {
    const newSchedule = form.schedule.filter((_, i) => i !== index);
    setForm({ ...form, schedule: newSchedule });
  };

  // 🔹 Sponsors
  const handleSponsorChange = (index, field, value) => {
    const newSponsors = [...form.sponsors];
    newSponsors[index][field] = value;
    setForm({ ...form, sponsors: newSponsors });
  };
  const addSponsor = () =>
    setForm({ ...form, sponsors: [...form.sponsors, { name: "", logo: "" }] });
  const removeSponsor = (index) => {
    const newSponsors = form.sponsors.filter((_, i) => i !== index);
    setForm({ ...form, sponsors: newSponsors });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await API.put("/about", form);
      alert("✅ About page updated!");
    } catch (err) {
      console.error(err);
      alert("❌ Error updating page");
    }
  };

  return (
    <div className="container my-5">
      <h3 className="mb-4">Edit About Page</h3>
      <form onSubmit={handleSubmit}>
        {/* Poster & Description */}
        <div className="mb-3">
          <label>Poster URL</label>
          <input
            type="url"
            name="poster"
            value={form.poster}
            onChange={handleChange}
            className="form-control"
          />
        </div>
        <div className="mb-3">
          <label>Description</label>
          <textarea
            name="description"
            value={form.description}
            onChange={handleChange}
            className="form-control"
          />
        </div>

        {/* Gallery */}
        <h5>Gallery</h5>
        {form.gallery.map((img, idx) => (
          <div key={idx} className="d-flex mb-2 gap-2">
            <input
              type="url"
              value={img}
              onChange={(e) => handleGalleryChange(idx, e.target.value)}
              className="form-control"
              placeholder={`Image URL ${idx + 1}`}
            />
            <button type="button" onClick={() => removeGallery(idx)} className="btn btn-danger">
              ❌
            </button>
          </div>
        ))}
        <button type="button" onClick={addGallery} className="btn btn-primary mb-3">
          + Add Image
        </button>

        {/* Schedule */}
        <h5>Schedule</h5>
        {form.schedule.map((s, idx) => (
          <div key={idx} className="d-flex mb-2 gap-2">
            <input
              type="date"
              value={s.date}
              onChange={(e) => handleScheduleChange(idx, "date", e.target.value)}
              className="form-control"
            />
            <input
              type="text"
              placeholder="Event Name"
              value={s.event}
              onChange={(e) => handleScheduleChange(idx, "event", e.target.value)}
              className="form-control"
            />
            <input
              type="time"
              value={s.time}
              onChange={(e) => handleScheduleChange(idx, "time", e.target.value)}
              className="form-control"
            />
            <button type="button" onClick={() => removeSchedule(idx)} className="btn btn-danger">
              ❌
            </button>
          </div>
        ))}
        <button type="button" onClick={addSchedule} className="btn btn-primary mb-3">
          + Add Schedule
        </button>

        {/* Sponsors */}
        <h5>Sponsors</h5>
        {form.sponsors.map((s, idx) => (
          <div key={idx} className="d-flex mb-2 gap-2">
            <input
              type="text"
              placeholder="Sponsor Name"
              value={s.name}
              onChange={(e) => handleSponsorChange(idx, "name", e.target.value)}
              className="form-control"
            />
            <input
              type="url"
              placeholder="Logo URL"
              value={s.logo}
              onChange={(e) => handleSponsorChange(idx, "logo", e.target.value)}
              className="form-control"
            />
            <button type="button" onClick={() => removeSponsor(idx)} className="btn btn-danger">
              ❌
            </button>
          </div>
        ))}
        <button type="button" onClick={addSponsor} className="btn btn-primary mb-3">
          + Add Sponsor
        </button>

        <button type="submit" className="btn btn-success mt-3">
          Save About Page
        </button>
      </form>
    </div>
  );
}
